import React, { useContext } from "react";
import classes from './Filter.module.css';
import iconRemove from '../../../images/icon-remove.svg';
import ListJobContext from "../../../store/JobsContext";



const Filter = (props) => {

  const jobsCtx = useContext(ListJobContext);


  return (
    <div className={classes['filter-container']}>

      <div className={classes.filters}>
        {jobsCtx.filterArray.map(item =>         <div className={classes['filter-item']}>

          <div className={classes['filter-item__name']}>
            {props.onName}
          </div>

          <div className={classes['filter-item__action']}>
            <img src={iconRemove} alt='remove icon'></img>
          </div>

        </div>)}
      </div>

      <div className={classes.clear}>clear</div>
    </div>
  )
}

export default Filter;
